import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    Paper, Button, TextField, IconButton, Select, MenuItem, InputLabel, FormControl
} from '@mui/material';
import {
    Add as AddIcon, Edit as EditIcon, FileCopy as FileCopyIcon, Search as SearchIcon
} from '@mui/icons-material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
}));

const Pacientes = () => {
    const classes = useStyles();
    const [data, setData] = useState([]);
    const [formState, setFormState] = useState({
        idpersona: '',
        apaterno: '',
        amaterno: '',
        nombre: '',
        fecha_nacimiento: '',
        sexo: 'M',
        estado_civil: 'S',
        tipo_documento: 'DNI',
        num_documento: '',
        direccion: '',
        telefono: '',
        email: '',
        ocupacion: '',
        persona_responsable: '',
        alergia: '',
        intervenciones_quirurgicas: '',
        vacunas_completas: 'SI'
    });
    const [isFormVisible, setIsFormVisible] = useState(false);
    const [isHistoriaVisible, setIsHistoriaVisible] = useState(false);
    const [historiaData, setHistoriaData] = useState([]);
    const [searchText, setSearchText] = useState('');

    useEffect(() => {
        fetchData();
    }, [searchText]);

    const fetchData = async () => {
        try {
            const response = await axios.get(`http://localhost:3002/api/persona/listar`, {
                params: { texto: searchText }
            });
            setData(response.data.data);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }
    };

    const handleInputChange = (e) => {
        setFormState({
            ...formState,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3002/api/persona/guardaryeditar', formState);
            alert(response.data.message);
            setIsFormVisible(false);
            fetchData();
        } catch (error) {
            console.error('Error saving data: ', error);
        }
    };

    const handleEdit = async (id) => {
        try {
            const response = await axios.get(`http://localhost:3002/api/persona/mostrar/${id}`);
            setFormState(response.data);
            setIsFormVisible(true);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }
    };

    const handleHistoria = async (id) => {
        try {
            const response = await axios.get(`http://localhost:3002/api/atencion/ListarAtenciones/${id}`);
            setHistoriaData(response.data);
            setIsHistoriaVisible(true);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }
    };

    return (
        <div>
            <h1>Pacientes</h1>
            <Button
                variant="contained"
                color="primary"
                startIcon={<AddIcon />}
                onClick={() => setIsFormVisible(true)}
            >
                Agregar
            </Button>
            <div>
                <TextField
                    label="Apellido Paterno o Materno o DNI"
                    variant="outlined"
                    value={searchText}
                    onChange={(e) => setSearchText(e.target.value)}
                />
                <Button
                    variant="contained"
                    color="default"
                    startIcon={<SearchIcon />}
                    onClick={fetchData}
                >
                    Buscar
                </Button>
            </div>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Opciones</TableCell>
                            <TableCell>Apellido Paterno</TableCell>
                            <TableCell>Apellido Materno</TableCell>
                            <TableCell>Nombre</TableCell>
                            <TableCell>Fecha de Nacimiento</TableCell>
                            <TableCell>Sexo</TableCell>
                            <TableCell>Estado Civil</TableCell>
                            <TableCell>Numero de Documento</TableCell>
                            <TableCell>Direccion</TableCell>
                            <TableCell>Teléfono</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((row) => (
                            <TableRow key={row.idpersona}>
                                <TableCell>
                                    <IconButton onClick={() => handleEdit(row.idpersona)}>
                                        <EditIcon />
                                    </IconButton>
                                    <IconButton onClick={() => handleHistoria(row.idpersona)}>
                                        <FileCopyIcon />
                                    </IconButton>
                                </TableCell>
                                <TableCell>{row.apaterno}</TableCell>
                                <TableCell>{row.amaterno}</TableCell>
                                <TableCell>{row.nombre}</TableCell>
                                <TableCell>{row.fecha_nacimiento}</TableCell>
                                <TableCell>{row.sexo}</TableCell>
                                <TableCell>{row.estado_civil}</TableCell>
                                <TableCell>{row.num_documento}</TableCell>
                                <TableCell>{row.direccion}</TableCell>
                                <TableCell>{row.telefono}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {isFormVisible && (
                <div>
                    <h2>{formState.idpersona ? 'Editar Paciente' : 'Agregar Paciente'}</h2>
                    <form onSubmit={handleSubmit}>
                        <TextField
                            label="Apellido Paterno"
                            name="apaterno"
                            value={formState.apaterno}
                            onChange={handleInputChange}
                            required
                        />
                        <TextField
                            label="Apellido Materno"
                            name="amaterno"
                            value={formState.amaterno}
                            onChange={handleInputChange}
                            required
                        />
                        <TextField
                            label="Nombre"
                            name="nombre"
                            value={formState.nombre}
                            onChange={handleInputChange}
                            required
                        />
                        <TextField
                            label="Fecha de Nacimiento"
                            name="fecha_nacimiento"
                            type="date"
                            value={formState.fecha_nacimiento}
                            onChange={handleInputChange}
                            InputLabelProps={{ shrink: true }}
                            required
                        />
                        <FormControl className={classes.formControl}>
                            <InputLabel>Sexo</InputLabel>
                            <Select
                                name="sexo"
                                value={formState.sexo}
                                onChange={handleInputChange}
                                required
                            >
                                <MenuItem value="M">Masculino</MenuItem>
                                <MenuItem value="F">Femenino</MenuItem>
                            </Select>
                        </FormControl>
                        <FormControl className={classes.formControl}>
                            <InputLabel>Estado Civil</InputLabel>
                            <Select
                                name="estado_civil"
                                value={formState.estado_civil}
                                onChange={handleInputChange}
                                required
                            >
                                <MenuItem value="S">Soltero</MenuItem>
                                <MenuItem value="C">Casado</MenuItem>
                                <MenuItem value="V">Viudo</MenuItem>
                                <MenuItem value="D">Divorciado</MenuItem>
                            </Select>
                        </FormControl>
                        <FormControl className={classes.formControl}>
                            <InputLabel>Tipo Documento</InputLabel>
                            <Select
                                name="tipo_documento"
                                value={formState.tipo_documento}
                                onChange={handleInputChange}
                                required
                            >
                                <MenuItem value="DNI">DNI</MenuItem>
                                <MenuItem value="RUC">RUC</MenuItem>
                                <MenuItem value="CEDULA">CEDULA</MenuItem>
                            </Select>
                        </FormControl>
                        <TextField
                            label="Número Documento"
                            name="num_documento"
                            value={formState.num_documento}
                            onChange={handleInputChange}
                            required
                        />
                        <TextField
                            label="Dirección"
                            name="direccion"
                            value={formState.direccion}
                            onChange={handleInputChange}
                        />
                        <TextField
                            label="Teléfono"
                            name="telefono"
                            value={formState.telefono}
                            onChange={handleInputChange}
                        />
                        <TextField
                            label="Email"
                            name="email"
                            value={formState.email}
                            onChange={handleInputChange}
                        />
                        <TextField
                            label="Ocupación"
                            name="ocupacion"
                            value={formState.ocupacion}
                            onChange={handleInputChange}
                        />
                        <TextField
                            label="Persona Responsable"
                            name="persona_responsable"
                            value={formState.persona_responsable}
                            onChange={handleInputChange}
                        />
                        <TextField
                            label="Alergias"
                            name="alergia"
                            value={formState.alergia}
                            onChange={handleInputChange}
                        />
                        <TextField
                            label="Intervenciones quirúrgicas"
                            name="intervenciones_quirurgicas"
                            value={formState.intervenciones_quirurgicas}
                            onChange={handleInputChange}
                        />
                        <FormControl className={classes.formControl}>
                            <InputLabel>Vacunas completas</InputLabel>
                            <Select
                                name="vacunas_completas"
                                value={formState.vacunas_completas}
                                onChange={handleInputChange}
                                required
                            >
                                <MenuItem value="SI">SI</MenuItem>
                                <MenuItem value="NO">NO</MenuItem>
                            </Select>
                        </FormControl>
                        <Button type="submit" variant="contained" color="primary">
                            Guardar
                        </Button>
                        <Button variant="contained" color="secondary" onClick={() => setIsFormVisible(false)}>
                            Cancelar
                        </Button>
                    </form>
                </div>
            )}

            {isHistoriaVisible && (
                <div>
                    <h2>Historia Clínica</h2>
                    <TableContainer component={Paper}>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Fecha</TableCell>
                                    <TableCell>Historia</TableCell>
                                    <TableCell>Receta</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {historiaData.map((row, index) => (
                                    <TableRow key={index}>
                                        <TableCell>{row.fecha}</TableCell>
                                        <TableCell>
                                            <a href={`../reportes/historia.php?idatencion=${row.idatencion}`} target="_blank" rel="noopener noreferrer">
                                                {row.idatencion}
                                            </a>
                                        </TableCell>
                                        <TableCell>
                                            <a href={`../reportes/receta.php?idatencion=${row.idatencion}`} target="_blank" rel="noopener noreferrer">
                                                {row.idatencion}
                                            </a>
                                        </TableCell>
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                    <Button variant="contained" color="secondary" onClick={() => setIsHistoriaVisible(false)}>
                        Cerrar
                    </Button>
                </div>
            )}
        </div>
    );
};

export default Pacientes;
