import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Escritorio from './components/Escritorio';
import ProtectedRoute from './components/ProtectedRoute';
import DashboardLayout from './components/DashboardLayout';
import NoAccess from './components/NoAccess';
import './App.css';

const App = () => {
    const [token, setToken] = useState(localStorage.getItem('token') || '');
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);

    useEffect(() => {
        if (token && user) {
            // Redirecciona según los permisos del usuario
            if (!user.escritorio) {
                window.location.href = '/no-access';
            }
        }
    }, [token, user]);

    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login setToken={setToken} setUser={setUser} />} />
                <Route path="/no-access" element={<NoAccess />} />
                <Route path="/" element={<ProtectedRoute><DashboardLayout /></ProtectedRoute>}>
                    <Route index element={<Navigate to={user?.escritorio ? "/escritorio" : "/no-access"} />} />
                    <Route path="escritorio" element={<ProtectedRoute requiredPermission="escritorio"><Escritorio /></ProtectedRoute>} />
                    {/* Agrega aquí las otras rutas protegidas */}
                </Route>
            </Routes>
        </Router>
    );
};

export default App;
