import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useTable } from 'react-table';


const Pacientes = () => {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:3002/api/persona/listar', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setData(response.data.data);
            } catch (error) {
                console.error('Error fetching data:', error.response ? error.response.data : error.message);
                setError(error.response ? error.response.data.message : error.message);
            }
        };

        fetchData();
    }, []);

    const columns = React.useMemo(() => [
        { Header: 'Apellido Paterno', accessor: 'apaterno' },
        { Header: 'Apellido Materno', accessor: 'amaterno' },
        { Header: 'Nombre', accessor: 'nombre' },
        { Header: 'Fecha de Nacimiento', accessor: 'fecha_nacimiento' },
        { Header: 'Sexo', accessor: 'sexo' },
        { Header: 'Estado Civil', accessor: 'estado_civil' },
        { Header: 'Número de Documento', accessor: 'num_documento' },
        { Header: 'Dirección', accessor: 'direccion' },
        { Header: 'Teléfono', accessor: 'telefono' },
    ], []);

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({ columns, data });

    return (
        <div className="container mt-5">
            <h1>Pacientes</h1>
            {error && <p className="text-danger">{error}</p>}
            <table {...getTableProps()} className="table table-striped">
                <thead>
                {headerGroups.map(headerGroup => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map(column => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                    </tr>
                ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                {rows.map(row => {
                    prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map(cell => (
                                <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                            ))}
                        </tr>
                    );
                })}
                </tbody>
            </table>
        </div>
    );
};

export default Pacientes;
