import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Button, Dialog, DialogTitle, DialogContent, DialogActions } from '@mui/material';

const ConfiguracionForm = ({ open, onClose, currentConfig, refreshData }) => {
    const [configData, setConfigData] = useState({
        idconfiguracion: '',
        razon_social: '',
        ruc: '',
        email: '',
        telefono: '',
        direccion: '',
        responsable: ''
    });

    useEffect(() => {
        if (currentConfig) {
            setConfigData(currentConfig);
        }
    }, [currentConfig]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setConfigData({
            ...configData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3002/api/configuracion/guardaryeditar', configData);
            console.log(response.data);
            refreshData();
            onClose();
        } catch (error) {
            console.error("Error saving configuration:", error);
        }
    };

    return (
        <Dialog open={open} onClose={onClose}>
            <DialogTitle>Configuración</DialogTitle>
            <DialogContent>
                <form onSubmit={handleSubmit}>
                    <TextField
                        label="Razón Social"
                        name="razon_social"
                        value={configData.razon_social}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="RUC"
                        name="ruc"
                        value={configData.ruc}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Email"
                        name="email"
                        value={configData.email}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Teléfono"
                        name="telefono"
                        value={configData.telefono}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Dirección"
                        name="direccion"
                        value={configData.direccion}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <TextField
                        label="Responsable"
                        name="responsable"
                        value={configData.responsable}
                        onChange={handleChange}
                        fullWidth
                        margin="normal"
                    />
                    <DialogActions>
                        <Button onClick={onClose} color="secondary">Cancelar</Button>
                        <Button type="submit" color="primary">Guardar</Button>
                    </DialogActions>
                </form>
            </DialogContent>
        </Dialog>
    );
};

export default ConfiguracionForm;
