import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useTable } from 'react-table';
import 'react-table/react-table.css';

const Pacientes = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token'); // Obtén el token del localStorage
            const response = await axios.get('http://localhost:3002/api/persona/listar', {
                headers: {
                    'Authorization': `Bearer ${token}` // Incluye el token en los encabezados
                }
            });
            setData(response.data.data);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const columns = React.useMemo(
        () => [
            {
                Header: 'Opciones',
                accessor: 'idpersona', // Accessor is the "key" in the data
                Cell: ({ value }) => (
                    <div>
                        // button className="btn btn-warning" onClick={() => mostrar(value)}>
                            <i className="fa fa-pencil"></i> Editar
                       // /button>
                        //button className="btn btn-info" onClick={() => historia(value)}>
                            <i className="fa fa-file"></i> Historia
                        ///button>
                    </div>
                ),
            },
            {
                Header: 'Apellido Paterno',
                accessor: 'apaterno',
            },
            {
                Header: 'Apellido Materno',
                accessor: 'amaterno',
            },
            {
                Header: 'Nombre',
                accessor: 'nombre',
            },
            {
                Header: 'Fecha de Nacimiento',
                accessor: 'fecha_nacimiento',
            },
            {
                Header: 'Sexo',
                accessor: 'sexo',
            },
            {
                Header: 'Estado Civil',
                accessor: 'estado_civil',
            },
            {
                Header: 'Número de Documento',
                accessor: 'num_documento',
            },
            {
                Header: 'Dirección',
                accessor: 'direccion',
            },
            {
                Header: 'Teléfono',
                accessor: 'telefono',
            },
        ],
        []
    );

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({ columns, data });

    return (
        <div>
            <h1>Listado de Pacientes</h1>
            {loading ? (
                <p>Cargando...</p>
            ) : (
                <table {...getTableProps()} className="react-table">
                    <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                        </tr>
                    ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                    {rows.map(row => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map(cell => {
                                    return (
                                        <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                    );
                                })}
                            </tr>
                        );
                    })}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Pacientes;
