import React, { useState, useEffect } from 'react';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';
import { Button } from '@mui/material';
import AddIcon from '@mui/icons-material/Add';

const Pacientes = () => {
    const [pacientes, setPacientes] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async (searchText = '') => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:3002/api/persona/listar', {
                headers: {
                    Authorization: `Bearer ${token}`
                },
                params: {
                    texto: searchText
                }
            });
            setPacientes(response.data.data);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }
    };

    const columns = [
        {
            field: 'opciones',
            headerName: 'Opciones',
            renderCell: (params) => (
                <div>
                    <Button
                        variant="contained"
                        color="warning"
                        onClick={() => console.log('Editar', params.row.idpersona)}
                    >
                        Editar
                    </Button>
                    <Button
                        variant="contained"
                        color="info"
                        onClick={() => console.log('Ver Historia', params.row.idpersona)}
                    >
                        Ver Historia
                    </Button>
                </div>
            ),
            sortable: false,
            filterable: false,
            width: 200,
        },
        { field: 'apaterno', headerName: 'Apellido Paterno', width: 150 },
        { field: 'amaterno', headerName: 'Apellido Materno', width: 150 },
        { field: 'nombre', headerName: 'Nombre', width: 150 },
        { field: 'fecha_nacimiento', headerName: 'Fecha de Nacimiento', width: 150 },
        { field: 'sexo', headerName: 'Sexo', width: 100 },
        { field: 'estado_civil', headerName: 'Estado Civil', width: 150 },
        { field: 'num_documento', headerName: 'Numero de Documento', width: 150 },
        { field: 'direccion', headerName: 'Direccion', width: 200 },
        { field: 'telefono', headerName: 'Teléfono', width: 150 },
    ];

    return (
        <div>
            <h1>Paciente</h1>
            <Button
                variant="contained"
                color="success"
                startIcon={<AddIcon />}
                onClick={() => console.log('Agregar')}
            >
                Agregar
            </Button>
            <div style={{ height: 400, width: '100%' }}>
                <DataGrid
                    rows={pacientes}
                    columns={columns}
                    pageSize={5}
                    rowsPerPageOptions={[5, 10, 20]}
                    checkboxSelection
                    disableSelectionOnClick
                    filterModel={{
                        items: [{ columnField: 'nombre', operatorValue: 'contains', value: '' }],
                    }}
                    onFilterModelChange={(model) => console.log(model)}
                />
            </div>
        </div>
    );
};

export default Pacientes;
