import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'datatables.net-dt/css/jquery.dataTables.css';
import $ from 'jquery';
import 'datatables.net';

const Pacientes = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const token = localStorage.getItem('token'); // Obtén el token del localStorage
            const response = await axios.get('http://localhost:3002/api/persona/listar', {
                headers: {
                    'Authorization': `Bearer ${token}` // Incluye el token en los encabezados
                }
            });
            setData(response.data.data);
            setLoading(false);

            // Inicializar DataTables
            $(document).ready(function() {
                $('#tblpacientes').DataTable();
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    return (
        <div>
            <h1>Listado de Pacientes</h1>
            {loading ? (
                <p>Cargando...</p>
            ) : (
                <table id="tblpacientes" className="display">
                    <thead>
                    <tr>
                        <th>Opciones</th>
                        <th>Apellido Paterno</th>
                        <th>Apellido Materno</th>
                        <th>Nombre</th>
                        <th>Fecha de Nacimiento</th>
                        <th>Sexo</th>
                        <th>Estado Civil</th>
                        <th>Número de Documento</th>
                        <th>Dirección</th>
                        <th>Teléfono</th>
                    </tr>
                    </thead>
                    <tbody>
                    {data.map((paciente, index) => (
                        <tr key={index}>
                            <td>
                                <button className="btn btn-warning" onClick={() => mostrar(paciente.idpersona)}>
                                    <i className="fa fa-pencil"></i> Editar
                                </button>
                                <button className="btn btn-info" onClick={() => historia(paciente.idpersona)}>
                                    <i className="fa fa-file"></i> Historia
                                </button>
                            </td>
                            <td>{paciente.apaterno}</td>
                            <td>{paciente.amaterno}</td>
                            <td>{paciente.nombre}</td>
                            <td>{paciente.fecha_nacimiento}</td>
                            <td>{paciente.sexo}</td>
                            <td>{paciente.estado_civil}</td>
                            <td>{paciente.num_documento}</td>
                            <td>{paciente.direccion}</td>
                            <td>{paciente.telefono}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Pacientes;
