import React, { useState, useEffect, useMemo } from 'react';
import { useTable, usePagination, useFilters, useGlobalFilter } from 'react-table';
import axios from 'axios';
import './Pacientes.css';

const Pacientes = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            const token = localStorage.getItem('token'); // Obtener el token del localStorage
            try {
                const result = await axios.get('http://localhost:3002/api/persona/listar', {
                    headers: {
                        'Authorization': `Bearer ${token}` // Incluir el token en el encabezado
                    }
                });
                setData(result.data.aaData);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching data:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const columns = useMemo(
        () => [
            {
                Header: 'Opciones',
                accessor: 'idpersona',
                Cell: ({ value }) => (
                    <div>
                        <button title="Editar" className="btn btn-warning" onClick={() => mostrar(value)}>
                            <i className="fa fa-pencil"></i>
                        </button>
                        <button title="Historia Clínicas" className="btn btn-info" onClick={() => historia(value)}>
                            <i className="fa fa-file"></i>
                        </button>
                    </div>
                )
            },
            { Header: 'Apellido Paterno', accessor: 'apaterno' },
            { Header: 'Apellido Materno', accessor: 'amaterno' },
            { Header: 'Nombre', accessor: 'nombre' },
            { Header: 'Fecha de Nacimiento', accessor: 'fecha_nacimiento' },
            { Header: 'Sexo', accessor: 'sexo' },
            { Header: 'Estado Civil', accessor: 'estado_civil' },
            { Header: 'Número de Documento', accessor: 'num_documento' },
            { Header: 'Dirección', accessor: 'direccion' },
            { Header: 'Teléfono', accessor: 'telefono' }
        ],
        []
    );

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        prepareRow,
        page,
        canPreviousPage,
        canNextPage,
        pageOptions,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        state: { pageIndex, pageSize, globalFilter },
        setGlobalFilter
    } = useTable(
        {
            columns,
            data,
            initialState: { pageIndex: 0 } // Set default page index to 0
        },
        useFilters,
        useGlobalFilter,
        usePagination
    );

    const limpiarForm = () => {
        // Implementación de la función para limpiar el formulario
    };

    const mostrarForm = (flag) => {
        // Implementación de la función para mostrar el formulario
    };

    const guardarYEditar = (e) => {
        // Implementación de la función para guardar o editar los datos
    };

    const mostrar = (idpersona) => {
        // Implementación de la función para mostrar los datos de una persona específica
    };

    const historia = (idpersona) => {
        // Implementación de la función para mostrar la historia clínica de una persona específica
    };

    return (
        <div>
            <div id="listadoregistros">
                <button onClick={() => mostrarForm(true)}>Agregar</button>
                <input
                    type="text"
                    value={globalFilter || ''}
                    onChange={(e) => setGlobalFilter(e.target.value)}
                    placeholder="Buscar..."
                />
                <table {...getTableProps()} className="table">
                    <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                        </tr>
                    ))}
                    </thead>
                    <tbody {...getTableBodyProps()}>
                    {loading ? (
                        <tr>
                            <td colSpan="10">Cargando...</td>
                        </tr>
                    ) : (
                        page.map(row => {
                            prepareRow(row);
                            return (
                                <tr {...row.getRowProps()}>
                                    {row.cells.map(cell => (
                                        <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                    ))}
                                </tr>
                            );
                        })
                    )}
                    </tbody>
                </table>
                <div>
                    <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
                        {'<<'}
                    </button>
                    <button onClick={() => previousPage()} disabled={!canPreviousPage}>
                        {'<'}
                    </button>
                    <button onClick={() => nextPage()} disabled={!canNextPage}>
                        {'>'}
                    </button>
                    <button onClick={() => gotoPage(pageOptions.length - 1)} disabled={!canNextPage}>
                        {'>>'}
                    </button>
                    <span>
                        Página{' '}
                        <strong>
                            {pageIndex + 1} de {pageOptions.length}
                        </strong>{' '}
                    </span>
                    <span>
                        | Ir a la página:{' '}
                        <input
                            type="number"
                            defaultValue={pageIndex + 1}
                            onChange={(e) => {
                                const page = e.target.value ? Number(e.target.value) - 1 : 0;
                                gotoPage(page);
                            }}
                            style={{ width: '100px' }}
                        />
                    </span>
                    <select
                        value={pageSize}
                        onChange={(e) => {
                            setPageSize(Number(e.target.value));
                        }}
                    >
                        {[5, 10, 20].map(pageSize => (
                            <option key={pageSize} value={pageSize}>
                                Mostrar {pageSize}
                            </option>
                        ))}
                    </select>
                </div>
            </div>
        </div>
    );
};

export default Pacientes;
