import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Pacientes.css';

const Pacientes = () => {
    const [pacientes, setPacientes] = useState([]);
    const [formData, setFormData] = useState({
        idpersona: '',
        apaterno: '',
        amaterno: '',
        nombre: '',
        fecha_nacimiento: '',
        sexo: 'M',
        estado_civil: 'S',
        tipo_documento: 'DNI',
        num_documento: '',
        direccion: '',
        telefono: '',
        email: '',
        ocupacion: '',
        persona_responsable: '',
        alergia: '',
        intervenciones_quirurgicas: '',
        vacunas_completas: 'SI'
    });
    const [isFormVisible, setIsFormVisible] = useState(false);

    const listar = async () => {
        try {
            const response = await axios.get('http://localhost:3002/api/persona/listar');
            setPacientes(response.data);
        } catch (error) {
            console.error('Error fetching pacientes:', error);
        }
    };

    useEffect(() => {
        listar();
    }, []);

    const limpiar = () => {
        setFormData({
            idpersona: '',
            apaterno: '',
            amaterno: '',
            nombre: '',
            fecha_nacimiento: '',
            sexo: 'M',
            estado_civil: 'S',
            tipo_documento: 'DNI',
            num_documento: '',
            direccion: '',
            telefono: '',
            email: '',
            ocupacion: '',
            persona_responsable: '',
            alergia: '',
            intervenciones_quirurgicas: '',
            vacunas_completas: 'SI'
        });
    };

    const mostrarForm = (flag) => {
        limpiar();
        setIsFormVisible(flag);
    };

    const guardarYEditar = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3002/api/persona/guardaryeditar', formData);
            alert(response.data.message);
            setIsFormVisible(false);
            listar();
        } catch (error) {
            console.error('Error saving paciente:', error);
        }
    };

    const mostrar = async (idpersona) => {
        try {
            const response = await axios.get(`http://localhost:3002/api/persona/mostrar/${idpersona}`);
            setFormData(response.data);
            setIsFormVisible(true);
        } catch (error) {
            console.error('Error fetching paciente:', error);
        }
    };

    const historia = async (idpersona) => {
        try {
            const response = await axios.get(`http://localhost:3002/api/atencion/ListarAtenciones/${idpersona}`);
            document.getElementById('hisotoriac').innerHTML = response.data.map((reg, index) => (
                `<tr key=${index}>
                    <td>${reg.fecha}</td>
                    <td><a target="_blank" href="../reportes/historia.php?idatencion=${reg.idatencion}">${reg.idatencion}</a></td>
                    <td><a target="_blank" href="../reportes/receta.php?idatencion=${reg.idatencion}">${reg.idatencion}</a></td>
                </tr>`
            )).join('');
            setIsFormVisible(false);
        } catch (error) {
            console.error('Error fetching historia:', error);
        }
    };

    return (
        <div className="pacientes-container">
            {isFormVisible ? (
                <div id="formularioregistros">
                    <form onSubmit={guardarYEditar}>
                        <input type="hidden" name="idpersona" value={formData.idpersona} />
                        {/* ...rest of the input fields similar to the original form, using formData and onChange handlers */}
                        <button type="submit">Guardar</button>
                        <button type="button" onClick={() => mostrarForm(false)}>Cancelar</button>
                    </form>
                </div>
            ) : (
                <div>
                    <button onClick={() => mostrarForm(true)}>Agregar</button>
                    <input type="text" id="texto" placeholder="Apellido Paterno o Materno o DNI" />
                    <button onClick={listar}>Buscar</button>
                    <table id="tbllistado">
                        <thead>
                        <tr>
                            <th>Opciones</th>
                            <th>Apellido Paterno</th>
                            <th>Apellido Materno</th>
                            <th>Nombre</th>
                            <th>Fecha de Nacimiento</th>
                            <th>Sexo</th>
                            <th>Estado Civil</th>
                            <th>Numero de Documento</th>
                            <th>Direccion</th>
                            <th>Teléfono</th>
                        </tr>
                        </thead>
                        <tbody>
                        {pacientes.map((paciente, index) => (
                            <tr key={index}>
                                <td>
                                    <button onClick={() => mostrar(paciente.idpersona)}>Editar</button>
                                    <button onClick={() => historia(paciente.idpersona)}>Historia</button>
                                </td>
                                <td>{paciente.apaterno}</td>
                                <td>{paciente.amaterno}</td>
                                <td>{paciente.nombre}</td>
                                <td>{paciente.fecha_nacimiento}</td>
                                <td>{paciente.sexo}</td>
                                <td>{paciente.estado_civil}</td>
                                <td>{paciente.num_documento}</td>
                                <td>{paciente.direccion}</td>
                                <td>{paciente.telefono}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            )}
            <div id="formulariohistoria" style={{ display: isFormVisible ? 'none' : 'block' }}>
                <table id="hisotoriac">
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Paciente</th>
                    </tr>
                    </thead>
                    <tbody>
                    {/* Aquí se insertará el contenido de la historia clínica */}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Pacientes;
